import axios from 'axios';
import React ,{ useEffect, useState } from 'react';
import './App.css';
import Nav from './component/Nav';
import CSVReader from './CSVReader.tsx';
import Test from './test.js';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import AddContact from './component/AddContact';
import uuid from 'react-uuid';
import api from "./api/contacts"

function App() {

const [rowData, setData] = useState([]);

const changeHandler = e => {
  console.log(e.target.files[0]);
}
const handleOnFileLoad = data => {
  console.log(rowData);
}

//load data when this page is loaded
useEffect(() => {getData()}, []);

const getData = async () => {
  axios.get(`http://localhost:5000/contacts`).then((res) => {
    const allData = res.data;

    console.log(allData);

    setData(allData);
  })
}

//  const getData = async () => {
//     const res = await api.get("/contacts", {headers: {'Access-Control-Allow-Origin' : '*',}});
//     api.head()
//     return res.data;
//  }

  // const addContactHandler = async (contact) => {
  //   console.log(contact);
  //   const req = {
  //     id:uuid(),
  //     ...rowData
  //   }

  //   const res = await api.post("/contacts", req);
  //   setData([...rowData, res.data]);
  // }

  const addContactHandler = async (contact) => {
    //  console.log(contact);
    const newData = {
      id: uuid(),
      contact
    }
    const res = await axios.post(`http://localhost:5000/contacts`, newData 
     ).then(res => {
      console.log(res);
     }).catch(e => {
      console.log(e);
     }) 

     setData([...rowData, res.data])


  }

  const removeContactHandler = async (id) => {
    await axios.delete(`http://localhost:5000/contacts/${id}`)
    const contact = rowData.filter((c) => {
      return c.id !== id;
    });

    setData(contact);
  }
  return (
    <div >
      <Nav/>

      <Container className="mt-5">
        <Row>
          <Col>
          <Test data={rowData}/>
          </Col>
          <Col>
          <h1>Please select a .csv file</h1>
          <CSVReader/>
          <button onClick={handleOnFileLoad}>Manually Upload</button>
          <button >Showdata</button>
          <AddContact addContactHandler={addContactHandler} /> 
          </Col>
        </Row>
      </Container>
      </div>

  );
}

export default App;
