          <CSVReader/>
          <button onClick={handleOnFileLoad}>Manually Upload</button>
          <button >Showdata</button>
          <AddContact addContactHandler={addContactHandler} /> 
          <UpdateContact updateContactHandler={updateContactHandler}/>
          </Col>
        </Row>
