import React from 'react'

class UpdateContact extends React.Component {
    constructor(props){
        super(props)
        const {id, name, email, phone} = props.location.state.contact
        this.state = {
            id,
            name,
            email,
            phone
        }
    }

    update = (e) => {
        e.preventDefault();
        if(this.state.name === "" 
            || this.state.email === ""
                || this.state.phone === ""){
                    alert("Please fill")
                }
        this.props.updateontactHandler(this.state);
        this.setState({name:"", email: "", phone: ""});
        this.props.history.push("/");
        console.log(this.state);
    }


    render(){
        return (
            <div>
            <h1>Add Contact</h1>
            <form onSubmit={this.update}>
            <label>Name</label>
            <input 
                type="text" 
                name='name' 
                value={this.state.name}
                onChange={ (e) => this.setState({name: e.target.value})}/><br/>
            <label>Email</label>
            <input 
                value={this.state.email}
                type="text" 
                onChange={ (e) => this.setState({email: e.target.value})}
                name='email'/><br/>
            <label>Phone</label>
            <input 
                type="text" 
                name='phone'
                value={this.state.phone}
                onChange={ (e) => this.setState({phone: e.target.value})}/><br/>
            <button>Add</button>
            </form>
            </div>
        )
    }
}

export default UpdateContact
