import React from 'react'

class UpdateContact extends React.Component {
        state = {
        id:"",
        name: "",
        email: "",
        phone: ""
    }


    update = (e) => {
        e.preventDefault();
        if(this.state.id === ""
            || this.state.name === "" 
                || this.state.email === ""
                    || this.state.phone === ""){
                    alert("Please fill")
                }
        this.props.updateContactHandler(this.state);

        this.setState({id:"", name:"", email: "", phone: ""});

    }


    render(){
        return (
