  // }

  const addContactHandler = async (contact) => {

    //  console.log(contact);
    const newData = {
      id: uuid(),
      contact
    }

    // fetch('http://localhost:5000/api',{
    //   method: 'POST',
    //   body: newData
    // }).then(res => {
    //   console.log(res);
    //   return res.json();
    // })

    const result = await axios.post('http://localhost:5000/api', { newData });
    console.log(result);
    // await axios({
    //   method: "POST",
